package com.neo.batch.compent;import com.neo.batch.builder.AbstractJob;import com.neo.batch.exception.BatchException;import org.springframework.stereotype.Component;import java.util.HashMap;import java.util.Map;/** * Created by cainiao on 2020-10-18 19:48 */@Componentpublic class JobHome {    private Map<String, AbstractJob> jobHome = new HashMap<>();    public void registry(String jobName,AbstractJob abstractJob){        jobHome.put(jobName,abstractJob);    }    public AbstractJob getJob(String jobName){        if (jobName==null){            new BatchException("JobHome can't found "+jobName+",Please check!");        }        return jobHome.get(jobName);    }}