package com.neo.batch.step;import com.neo.batch.tasklet.PersonEatTasklet;import org.springframework.batch.core.Step;import org.springframework.batch.core.configuration.annotation.EnableBatchProcessing;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;/** * Created by cainiao on 2020-10-18 19:42 */@Configuration@EnableBatchProcessingpublic class PersonEatStep extends BaseStep {    @Bean("personEatStepBean")    public Step personEatStep(){        return stepBuilderFactory.get("personEatStep")                .tasklet(getPersonEatTasklet())                .build();    }    public PersonEatTasklet getPersonEatTasklet(){        return new PersonEatTasklet();    }}