package com.neo.batch.step;import com.neo.batch.tasklet.PersonEatTasklet;import com.neo.batch.tasklet.PlayGameTasklet;import org.springframework.batch.core.Step;import org.springframework.batch.core.configuration.annotation.EnableBatchProcessing;import org.springframework.batch.core.configuration.annotation.StepScope;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;/** * Created by cainiao on 2020-10-18 19:42 */@Configuration@EnableBatchProcessingpublic class PlayGameStep extends BaseStep {    @Bean("playGameStepBean")    public Step playGameStep(){        return stepBuilderFactory.get("personEatStep")                .tasklet(getPlayGameTasklet())                .build();    }    @Bean    @StepScope    public PlayGameTasklet getPlayGameTasklet(){        return new PlayGameTasklet();    }}