package com.neo.batch.tasklet;import org.springframework.batch.core.StepContribution;import org.springframework.batch.core.scope.context.ChunkContext;import org.springframework.batch.core.step.tasklet.Tasklet;import org.springframework.batch.repeat.RepeatStatus;import org.springframework.beans.factory.annotation.Value;import java.util.Date;/** * Created by cainiao on 2020-10-18 19:44 */public class PersonEatTasklet implements Tasklet {    @Value("#{jobParameters[executeTime]}")    private Date executeTime;    @Override    public RepeatStatus execute(StepContribution contribution, ChunkContext chunkContext) throws Exception {        chunkContext.getStepContext().getStepExecution().getExecutionContext().put("executeJobInFo",executeTime);        System.out.println("吃东西"+executeTime);        return null;    }}