package com.neo.batch.tasklet;import org.springframework.batch.core.StepContribution;import org.springframework.batch.core.scope.context.ChunkContext;import org.springframework.batch.core.step.tasklet.Tasklet;import org.springframework.batch.item.ExecutionContext;import org.springframework.batch.repeat.RepeatStatus;import org.springframework.beans.factory.annotation.Value;import java.util.Date;/** * Created by cainiao on 2020-10-18 19:44 */public class PlayGameTasklet implements Tasklet {    @Value("#{jobParameters[executeTime]}")    private Date executeTime;    @Value("#{stepExecution.executionContext}")    private ExecutionContext stepContext;    @Override    public RepeatStatus execute(StepContribution contribution, ChunkContext chunkContext) throws Exception {        String  executeJobInFo1 = (String) chunkContext.getStepContext().getStepExecution().getExecutionContext().get("executeJobInFo");        String executeJobInFo = (String) stepContext.get("executeJobInFo");        System.out.println("executeTime："+executeTime);        System.out.println("获取到的信息是："+executeJobInFo1);        return null;    }}