package com.neo.designmode.factory;/** * Created by cainiao on 2021/1/3 5:54 下午 * 具体工厂 */public class FruitFactory extends Factory{    @Override    Product createPrduct(Class clz) {        try {            Product product = (Product) Class.forName(clz.getClass().getName()).newInstance();            return product;        } catch (Exception e) {            e.printStackTrace();        }        return null;    }}