package com.neo.designmode.singleton;/** * Created by cainiao on 2020-10-09 22:05 */public class LazySingletonSync {    private static LazySingletonSync instance = null;    private LazySingletonSync() {    }    public static synchronized LazySingletonSync getInstance(){        if (instance==null){            instance = new LazySingletonSync();        }        return instance;    }}