package com.neo.designmode.singleton;/** * Created by cainiao on 2020-10-09 22:05 */public class LazySingletonSyncDubble {    private static LazySingletonSyncDubble instance = null;    private LazySingletonSyncDubble() {    }    public static LazySingletonSyncDubble getInstance(){        if (instance==null){            synchronized (LazySingletonSyncDubble.class){                if (instance==null){                    instance = new LazySingletonSyncDubble();                }            }        }        return instance;    }}