package com.neo.controller;import com.neo.entity.User;import com.neo.service.OrderService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;/** * Created by cainiao on 2020-09-18 00:20 */@RestControllerpublic class UserController {    @Autowired    OrderService orderService;    @RequestMapping("getUserlistVersinon1")    public List<User> getUserlistVersinon1(){        List<User> userlist = orderService.getUserlistVersinon1();        return userlist;    }    @RequestMapping("getUserlistVersinon2")    public List<User> getUserlistVersinon2(){        List<User> userlist = orderService.getUserlistVersinon2();        return userlist;    }}