package com.neo.controller;import com.neo.dao.master.PersonMapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.Map;/** * Created by cainiao on 2020-09-26 21:44 */@RestControllerpublic class OneDataSourceController {    @Autowired    private PersonMapper personMapper;    @RequestMapping("oneDataSource")    public Map<String, Object> queryPersonById(){        Map<String, Object> map = personMapper.queryPersonById();        return map;    }}