CREATE TABLE `person` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` varchar(255) DEFAULT NULL COMMENT '姓名',  `age` int(11) DEFAULT NULL COMMENT '年龄',  `gender` varchar(255) DEFAULT NULL COMMENT '籍贯',  `hometown` varchar(255) DEFAULT NULL COMMENT '家乡',  `create_time` datetime DEFAULT NULL,  `policy_no` varchar(255) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;