package com.neo;import com.neo.generator.student.entity.Student;import com.neo.generator.student.service.impl.StudentServiceImpl;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.test.context.junit4.SpringRunner;import java.util.ArrayList;import java.util.List;/** * Created by cainiao on 2021/1/7 10:46 下午 * * // TableId 注解存在更新记录，否插入一条记录 * boolean saveOrUpdate(T entity); * // 根据updateWrapper尝试更新，否继续执行saveOrUpdate(T)方法 * boolean saveOrUpdate(T entity, Wrapper<T> updateWrapper); * // 批量修改插入 * boolean saveOrUpdateBatch(Collection<T> entityList); * // 批量修改插入 * boolean saveOrUpdateBatch(Collection<T> entityList, int batchSize); * */@SpringBootTest@RunWith(SpringRunner.class)public class SampleSaveOrUpdate {    @Autowired    private StudentServiceImpl studentService;    @Test    public void saveOrUpdate(){        Student student = new Student();        student.setId(22l);        student.setName("张三新名字");        System.out.println(studentService.saveOrUpdate(student));    }    @Test    public void saveOrUpdateBatch(){        List<Student> list = new ArrayList<>();        Student student = new Student();        student.setId(22l);        student.setName("张三新222");        Student student1 = new Student();        student1.setId(23l);        student1.setName("张三新333");        list.add(student);        list.add(student1);        System.out.println(studentService.saveOrUpdateBatch(list));    }    public List<Student> createBatchStudent(String personName,int insertCount){        List<Student> list = new ArrayList<>();        for (int i=0;i<insertCount;i++){            Student student = new Student();            student.setName(personName+"_"+i);            student.setAge(String.valueOf(i));            list.add(student);        }        return list;    }}