package com.neo.thirteen;/** * Created by cainiao on 2020-08-22 14:46 * 函数式接口当形参 */public class FunctionalInterface {    /**     * 函数式接口当形参 Runnable 是一个函数式接口     * @param runnable     */    public static void startThread(Runnable runnable){        new Thread(runnable).run();    }    public static void main(String[] args) {        startThread(new Runnable() {            @Override            public void run() {                System.out.println(Thread.currentThread().getName()+"匿名内部类启动线程");            }        });        startThread(()-> System.out.println("Lambda方式启动线程"));    }}