package com.neo.thirteen;/** * Created by cainiao on 2020-08-22 13:21 */public class RecursiveFibonacci {    IntCall intCall;    public RecursiveFibonacci() {        intCall = n -> n==0 ? 0 :                       n==1 ? 1 :                       intCall.call(n-1)+intCall.call(n-2);    }    int outInfo(int i){        return intCall.call(i);    }    public static void main(String[] args) {        RecursiveFibonacci fibonacci = new RecursiveFibonacci();        for (int i=0;i<11;i++){            System.out.println(fibonacci.outInfo(i));        }    }}