package com.neo.thirteen;import java.text.SimpleDateFormat;/** * Created by cainiao on 2020-08-22 14:05 */public class TimeTest {    public static void main(String[] args) throws Exception {        long l = calcuDate("20200229", "20200301");        System.out.println(l);    }    public static long calcuDate(String begainDate,String endDate) throws Exception {        SimpleDateFormat sdf = new SimpleDateFormat("yyyyMMdd");        long begainTime = sdf.parse(begainDate).getTime();        long endTime = sdf.parse(endDate).getTime();        long l = (endTime - begainTime) / (24 * 60 * 60 * 1000);        return l;    }}