package com.neo.md;import java.util.ArrayList;import java.util.Iterator;import java.util.List;import java.util.concurrent.ConcurrentHashMap;import java.util.concurrent.CopyOnWriteArrayList;/** * Created by cainiao on 2020/12/23 11:09 下午 */public class ArrayTest {    public static void main(String[] args) {        List<Integer> list = new ArrayList<>();        for (int i=0;i<10;i++){            list.add(i);        }        System.out.println(list);        Iterator<Integer> iterator = list.iterator();        while (iterator.hasNext()){            Integer next = iterator.next();            if (next==6){                list.remove(iterator.next());            }            System.out.println(iterator.next());        }    }}