package com.neo.moretest.annotationtest;import org.springframework.context.annotation.Bean;import org.springframework.stereotype.Component;/** * Created by cainiao on 2020-08-12 21:24 */@Componentpublic class LiteConfig {    @Bean    public User user(){        return new User();    }    @Bean    public String name(User user){        System.out.println("user.hashCode():"+user.hashCode());        System.out.println("user().hashCode():"+user().hashCode());        System.out.println("again user().hashCode():"+user().hashCode());        return "test";    }}