package com.neo.pdf;/** * Created by cainiao on 2020/10/30 6:07 下午 */public class MyPdfException extends Exception{    public MyPdfException() {    }    public MyPdfException(String message) {        super(message);    }    public MyPdfException(String message, Throwable cause) {        super(message, cause);    }    public MyPdfException(Throwable cause) {        super(cause);    }    public MyPdfException(String message, Throwable cause, boolean enableSuppression, boolean writableStackTrace) {        super(message, cause, enableSuppression, writableStackTrace);    }}