package com.neo.ti;import java.util.HashMap;import java.util.Map;/** * Created by cainiao on 2021/4/10 3:01 下午 */public class ZiMu {    public static void main(String[] args) {        Map<Character,Integer> map = new HashMap<>();        int initCount=1;        System.out.println(getZiMu());        char[] chars = getZiMu().toCharArray();        for (char item:chars) {            Integer integer = map.get(Character.toLowerCase(item));            if (integer==null){                map.put(Character.toLowerCase(item),initCount);            } else {                integer = integer+1;                map.put(Character.toLowerCase(item),integer);            }        }        System.out.println(map);    }    public static String getZiMu() {        StringBuffer sb = new StringBuffer();        for (int i = 1; i < 26; i++) {            sb.append((char) (96 + i));        }        for (int i = 1; i < 26; i++) {            sb.append(Character.toUpperCase((char) (96 + i)));        }        return sb.reverse().toString();    }}