package com.neo.service;import com.neo.config.RabbitSender;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.scheduling.annotation.EnableScheduling;import org.springframework.scheduling.annotation.Scheduled;import org.springframework.stereotype.Component;import java.text.SimpleDateFormat;import java.util.Date;import java.util.HashMap;import java.util.Map;/** * Created by cainiao on 2021/3/7 11:31 上午 */@Component@EnableSchedulingpublic class ProductService {    @Autowired    private RabbitSender rabbitSender;    private static SimpleDateFormat simpleDateFormat = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss.SSS");    @Scheduled(cron = "0/5 * * * * ? ")    public void sendMessage(){        String time = simpleDateFormat.format(new Date());        Map<String, Object> properties = new HashMap<>();        properties.put("number", "12345");        properties.put("send_time", time);        System.out.println("生产者发送消息："+time);        try {            rabbitSender.send("Hello RabbitMQ For Spring Boot!", properties);        } catch (Exception e) {            e.printStackTrace();        }    }}