package com.neo;import com.neo.config.RabbitSender;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.test.context.junit4.SpringRunner;import java.text.SimpleDateFormat;import java.util.Date;import java.util.HashMap;import java.util.Map;/** * Created by cainiao on 2021/3/7 11:18 上午 */@RunWith(SpringRunner.class)@SpringBootTestpublic class FirstTest {    @Autowired    private RabbitSender rabbitSender;    private static SimpleDateFormat simpleDateFormat = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss.SSS");    @Test    public void testSender1() throws Exception {        Map<String, Object> properties = new HashMap<>();        properties.put("number", "12345");        properties.put("send_time", simpleDateFormat.format(new Date()));        rabbitSender.send("Hello RabbitMQ For Spring Boot!", properties);    }}