package com.neo.controller;import com.neo.model.UserInfo;import com.neo.service.RedisService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.Optional;/** * Created by cainiao on 2020-08-23 10:15 */@RestControllerpublic class RedisController {    @Autowired    private RedisService redisService;    @RequestMapping("getUserInfoById/redis/{id}")    public UserInfo getUserInfoById(@PathVariable("id") String id) throws InterruptedException {        UserInfo userInfo = redisService.getUserInfoById(id);        return userInfo;    }}