package com.neo.controller;import com.neo.model.UserInfo;import com.neo.service.SpringCacheService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;/** * Created by cainiao on 2020-08-23 10:15 */@RestControllerpublic class SpringCacheController {    @Autowired    private SpringCacheService springCacheService;    /**     * 增加缓存注解测试     * @param id     * @return     */    @RequestMapping("springCacheCacheable/{id}")    public UserInfo springCacheCacheable(@PathVariable("id") long id){        UserInfo userInfo = springCacheService.springCacheCacheable(id);        return userInfo;    }    /**     * 删除缓存注解测试     * @param id     * @return     */    @RequestMapping("springCacheCacheEvict/{id}")    public UserInfo springCacheCacheEvict(@PathVariable("id") long id){        UserInfo userInfo = springCacheService.springCacheCacheEvict(id);        return userInfo;    }}