package com.neo.spring.aware;import org.springframework.beans.BeansException;import org.springframework.beans.factory.BeanNameAware;import org.springframework.context.ApplicationContext;import org.springframework.context.ApplicationContextAware;import org.springframework.context.annotation.Configuration;/** * Created by cainiao on 2020/12/19 10:54 上午 */@Configurationpublic class DogAware implements ApplicationContextAware, BeanNameAware {    private String name;    @Override    public void setBeanName(String name) {        System.out.println(this.getClass().getName()+" setBeanName is "+name);    }    @Override    public void setApplicationContext(ApplicationContext applicationContext) throws BeansException {        System.out.println(this.getClass().getName()+" applicationContext is "+applicationContext);    }}